INSERT INTO `es_goods` VALUES ('94', '海牌 海苔2g*10(韩国)', '0009542692', '46', '33', '21', 'normal', null, '2.000', '1', 'http://javashop3.javamall.com.cn/statics/attachment/goods/201202231530584595.jpg', 'http://javashop3.javamall.com.cn/statics/attachment/goods/201202231530584595.jpg', null, '<p>\n	<font face=\"宋体\"><font size=\"3\"><font color=\"#800000\"><strong>商品特色</strong></font>&nbsp; <font color=\"#c0c0c0\">Brand</font></font></font></p>\n<p>\n	<font color=\"#800000\"><font color=\"#000000\" face=\"宋体\" size=\"2\" style=\"background-color: #ffffff\">紫菜的相关知识 </font></font></p>\n<p>\n	<font color=\"#800000\"><font color=\"#000000\" face=\"宋体\" size=\"2\" style=\"background-color: #ffffff\">海 牌海苔，香脆可口,做紫菜包饭、寿司、切成丝放在汤里面,或烤制吐司都会非常美味,咸度也很适中,所以即使您什么都不做直接拿着当零食吃也是非常棒的哦. 海苔素有维他命宝库之称,高蛋白、低热量、大量维生素A、B1、B2、C，并含有丰富的蛋白质、牛黄酸和矿物质等，有益于年轻女士美化肌肤，尤适于生长发 育期的少年儿童和年老体弱者食用.此外，海苔能延缓衰老，是风靡世界的健康食品。</font></font></p>\n<p>\n	<font color=\"#800000\"><font color=\"#000000\" face=\"宋体\" size=\"2\" style=\"background-color: #ffffff\">原材料采自天然无污染的海洋，现代先进加工工艺，保留天然风味。古代为高丽宫廷和两班贵族贡品。</font></font></p>\n<p>\n	<font color=\"#800000\"><font color=\"#000000\" face=\"宋体\" size=\"2\" style=\"background-color: #ffffff\">海 苔本身的营养很高，因为它生长在海边岩石上，充分汲取了海水中的精华，蛋白质、矿物质和维生素的含量极其丰富，被人们称为&ldquo;维生素的宝库&rdquo;。其中，胡萝卜 素、核黄素、维生素A、B族维生素的含量特别高.海苔中还含有铁、钙等矿物质.海苔的脂肪含量比较低，只占全部营养成分的1%&mdash;2%，但其中有利于神经系 统发育的不饱和脂肪酸EPA的含量就占到了其中的52%，再加上大量人体必需的矿物质和维生素，长期食用海苔能改善微循环、增强免疫力、延缓衰老、减少癌 症和心血管病的发病率。</font></font></p>\n<p>\n	<font color=\"#800000\"><font color=\"#000000\" face=\"宋体\" size=\"2\" style=\"background-color: #ffffff\">海苔和紫菜是一回事。我们常吃的紫菜主要包括两个品种：坛紫菜和条斑紫菜，北方以条斑紫菜为主，南方则以坛紫菜为主。海苔是用条斑紫菜加工而成。</font></font></p>\n', '9.300', '8.000', '19.200', '[{\"name\":\"基本参数\",\"paramList\":[{\"name\":\"规格(重量)\",\"value\":\"2g*10\",\"valueList\":[]},{\"name\":\"包装\",\"value\":\"包\",\"valueList\":[]},{\"name\":\"原产地\",\"value\":\"韩国\",\"valueList\":[]},{\"name\":\"保质期\",\"value\":\"365天\",\"valueList\":[]},{\"name\":\"储藏方法\",\"value\":\"存放于阴凉干爽处，避免阳光直射\",\"valueList\":[]},{\"name\":\"商品形态\",\"value\":\"固态\",\"valueList\":[]},{\"name\":\"食用方法\",\"value\":\"开启即用\",\"valueList\":[]},{\"name\":\"成分\",\"value\":\"海苔 菜油 香油 调味盐\",\"valueList\":[]}],\"paramNum\":8}]', '[{\"cost\":8,\"goodsLvPrices\":[],\"goods_id\":94,\"name\":\"海牌 海苔2g*10(韩国)\",\"price\":9.3,\"product_id\":94,\"sn\":\"0009542692\",\"specList\":[],\"specs\":\"\",\"specsvIdJson\":\"[]\",\"store\":0,\"weight\":2}]', null, null, '1329982371368', '1329982371368', '0', '0', '0', '0', '0', '', '', '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '韩国', '1', '0', '0', '0', '0', '0', null, '0', '0', '0', '0', '0');
