INSERT INTO `es_goods` VALUES ('117', 'OLAY玉兰油多效修护眼霜 15g ', '0000407454', '47', '45', '25', 'normal', null, '15.000', '1', 'http://javashop3.javamall.com.cn/statics/attachment/goods/201202231712456923.jpg', 'http://javashop3.javamall.com.cn/statics/attachment/goods/201202231712456923.jpg', null, '<p class=\"detail_tit\">\n	<strong>商品特色</strong></p>\n<p>\n	OLAY全新上市的&ldquo;多元修 护眼霜&rdquo;，针对女性消费者最在意的眼角细纹，达到全面性的改善，成份中含有维他命B3、B5、C、E、锌、镁、钠，等多元成分，可以让肤质新生，帮助促进 肌肤胶原蛋白增生，加速肌肤新陈代谢，提高其防御能力并改善暗沉的肤色。此外，绿茶、芦荟及小黄瓜萃取物成分，也能镇静舒缓眼周肌肤，改善因用眼过度导致 的眼部疲惫肿泡，再加上特殊瞬效光焕能抵抗7大眼部岁月问题、清爽不黏膩。&nbsp;</p>\n<p>\n	产品功效：</p>\n<p>\n	1.改善暗哑及令眼部明亮。<br />\n	2.均匀眼部肤色。<br />\n	3.使黑眼圈看起来不明显。<br />\n	4.令眼部细纹及皱纹看起来不明显。<br />\n	5.滋润眼部肌肤。<br />\n	6.软化眼部不平滑肌肤。<br />\n	7.减少眼部浮肿&nbsp; 每日早晚使用于洁肤后,取适量轻轻涂于眼周肌肤直至完全吸收。</p>\n', '94.700', '90.000', '120.000', '[{\"name\":\"基本参数\",\"paramList\":[{\"name\":\"规格\",\"value\":\"15g\",\"valueList\":[]},{\"name\":\"上架时间\",\"value\":\"2009-10-18\",\"valueList\":[]},{\"name\":\"成分\",\"value\":\"\",\"valueList\":[]},{\"name\":\"产地\",\"value\":\"中国\",\"valueList\":[]},{\"name\":\"保质期\",\"value\":\"1095天\",\"valueList\":[]}],\"paramNum\":5}]', '[{\"cost\":90,\"goodsLvPrices\":[],\"goods_id\":117,\"name\":\"OLAY玉兰油多效修护眼霜 15g \",\"price\":94.7,\"product_id\":117,\"sn\":\"0000407454\",\"specList\":[],\"specs\":\"\",\"specsvIdJson\":\"[]\",\"store\":0,\"weight\":15}]', null, null, '1329988455899', '1329988455899', '0', '0', '0', '0', '0', '', '', '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '修护', '0', '0', '0', '0', '0', null, '0', '0', '0', '0', '0');
