INSERT INTO `es_goods` VALUES ('130', '曼秀雷敦男士润唇膏（天然型）3.5g ', '0015704151', '57', '49', '26', 'normal', null, '3.500', '1', 'http://javashop3.javamall.com.cn/statics/attachment/goods/201202231907582746.jpg', 'http://javashop3.javamall.com.cn/statics/attachment/goods/201202231907582746.jpg', null, '<p class=\"detail_tit\">\n	<strong>品牌介绍</strong></p>\n<p>\n	<font face=\"宋体\" size=\"2\">&nbsp;&nbsp;&nbsp; 美国曼秀雷敦公司创立于1889年，其创办人爱拔.亚历山大.希尔先生当时与药剂师友人合资生产药物，经四年研究，成功创制具镇痛、止痒、伤风感冒及蚊虫咬伤疗效的&ldquo;曼秀雷敦薄荷膏&rdquo;，获医药公司和用家大力推荐，迅即成为美国家传户晓的必备良药。 </font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">&nbsp;&nbsp; &ldquo;曼秀雷敦&rdquo;英文名字&lsquo;MENTHOLATUM&rsquo;，是由&lsquo;MENTHOL<font face=\"宋体\" size=\"2\">&rsquo;</font>（薄 荷）及&lsquo;PETROLATUM&rsquo;(石腊油)组合而成。 百多年后，曼秀雷敦除生产薄荷膏外，还制造摩擦膏、润唇膏及止痛等内外用药品，畅销世界150多个国家，其分公司遍布世界各地，在加拿大、澳洲、英国及亚 洲太平洋地区均设有分厂。其产品于日本、香港及台湾等地迅即成为市场中之皇牌产品，占全球一般皮肤及外用药物市场之前列位置 。 </font></p>\n', '23.900', '22.000', '29.900', '[{\"name\":\"基本参数\",\"paramList\":[{\"name\":\"规格\",\"value\":\"3.5g\",\"valueList\":[]},{\"name\":\"上架时间\",\"value\":\"2011-10-13\",\"valueList\":[]},{\"name\":\"保质期\",\"value\":\"二年\",\"valueList\":[]},{\"name\":\"产地\",\"value\":\"中国\",\"valueList\":[]},{\"name\":\"成分\",\"value\":\"\",\"valueList\":[]}],\"paramNum\":5}]', '[{\"cost\":22,\"goodsLvPrices\":[],\"goods_id\":130,\"name\":\"曼秀雷敦男士润唇膏（天然型）3.5g \",\"price\":23.9,\"product_id\":130,\"sn\":\"0015704151\",\"specList\":[],\"specs\":\"\",\"specsvIdJson\":\"[]\",\"store\":0,\"weight\":3.5}]', null, null, '1329995345274', '1329995345274', '0', '0', '0', '100', '0', '', '', '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '滋润', '0', '0', '0', '0', '0', null, '0', '0', '0', '0', '0');
