INSERT INTO `es_goods` VALUES ('183', '强生婴儿口手湿巾（开心食刻）80片-原清爽洁肤柔湿巾80片经济装', '0000100083', '69', '64', '30', 'normal', null, '80.000', '1', 'http://javashop3.javamall.com.cn/statics/attachment/goods/201202241843438923.jpg', 'http://javashop3.javamall.com.cn/statics/attachment/goods/201202241843438923.jpg', null, '<p>\n	<font color=\"#800000\" face=\"黑体\" size=\"4\"><strong>商品介绍</strong></font>&nbsp; <font color=\"#c0c0c0\" size=\"4\">Product</font></p>\n<p>\n	<font color=\"#000000\" face=\"宋体\" size=\"2\">规格：80片</font></p>\n<p>\n	<font color=\"#000000\" face=\"宋体\" size=\"2\">保质天数：730</font></p>\n<p>\n	<font color=\"#000000\" face=\"宋体\" size=\"2\">产地：中国</font></p>\n<p>\n	<font color=\"#000000\" face=\"宋体\" size=\"2\">商品描述：<br />\n	1.柔软温和，适合清洁脸和手。<br />\n	2.蕴含婴儿润肤露,留下一层滋润保护层,保护宝宝肌肤的天然皮质不受破坏。<br />\n	3.特别添加CPC抗菌成分,抵御产生尿布疹的细菌,带宝宝出游的必需品。</font></p>\n<p>\n	<font color=\"#000000\" face=\"宋体\" size=\"2\">使用方法： 将包装上的密封粘纸撕开，从孔中将第一张湿巾抽出，即可使用，使用后再将粘纸贴回原位即可。</font></p>\n<p>\n	<font color=\"#000000\" face=\"宋体\" size=\"2\">注意事项： 避免宝宝误食请置于宝宝接触不到的地方，避免直接接触眼睛及周边皮肤，如有不适请暂停使用。本产品不能被溶解请勿丢入马桶内</font><font color=\"#000000\" face=\"宋体\" size=\"2\">。</font></p>\n<p>\n	<font color=\"#800000\" face=\"黑体\" size=\"4\"><strong><font color=\"#000000\" face=\"Arial\" size=\"3\" style=\"background-color: #c0c0c0\">&nbsp;</font>品牌介绍</strong></font>&nbsp; <font color=\"#c0c0c0\" size=\"4\">Brand</font></p>\n<p>\n	<font face=\"Arial\" size=\"2\"><font color=\"#000000\" face=\"宋体\">美国强生 (Johnson &amp; Johnson)成立于1886年，是世界上规模大、产品多元化的医疗卫生保健品及消费者护理产品公司。据《财富》和《商业周刊》97年公布的结果，强生 公司市场价值指标评比名列全球第20位，并位居全美十大最令人羡慕的公司之列，1999年全球营业额达275亿美元。目前强生在全球57个国家建立了 230多家分公司，是目前世界上最具综合性、业务分布范围最广的卫生保健产品的制造商和相关服务提供商，拥有约11万6千余名员工, 产品销售于175个国家和地区。 是世界最具综合性、分布范围最广的健康护理产品制造商、健康服务提供商。</font></font></p>\n', '15.100', '13.000', '15.900', '[{\"name\":\"基本参数\",\"paramList\":[{\"name\":\"规格\",\"value\":\"80片\",\"valueList\":[]},{\"name\":\"上架时间\",\"value\":\"\",\"valueList\":[]},{\"name\":\"保质天数\",\"value\":\"730\",\"valueList\":[]},{\"name\":\"产地\",\"value\":\"中国\",\"valueList\":[]}],\"paramNum\":4}]', '[{\"cost\":13,\"goodsLvPrices\":[],\"goods_id\":183,\"name\":\"强生婴儿口手湿巾（开心食刻）80片-原清爽洁肤柔湿巾80片经济装\",\"price\":15.1,\"product_id\":183,\"sn\":\"0000100083\",\"specList\":[],\"specs\":\"\",\"specsvIdJson\":\"[]\",\"store\":0,\"weight\":80}]', null, null, '1330080299540', '1330080299540', '0', '0', '0', '0', '0', '', '', '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2', '0', '0', '0', '0', '0', null, '0', '0', '0', '0', '0');
