INSERT INTO `es_goods` VALUES ('223', '嗳呵 儿童牙膏（乳牙）青苹果味50g', '0016095639', '79', '74', '37', 'normal', null, '50.000', '1', 'http://javashop3.javamall.com.cn/statics/attachment/goods/201202242126444827.jpg', 'http://javashop3.javamall.com.cn/statics/attachment/goods/201202242126444827.jpg', null, '<p class=\"detail_tit\">\n	<strong>商品特色</strong></p>\n<p>\n	嗳呵儿童牙膏的五大优势：<br />\n	●&nbsp; 采用蔓越莓粉，天然植物提取，有效补充乳牙成长所需营养，使乳牙更健康。<br />\n	● 采用食品级木糖醇，不含糖分，有效保护刚刚长出的乳牙。<br />\n	● 采用超软性磨料，有效保护牙釉质及稚嫩牙龈。<br />\n	●&nbsp; 低泡无氟配方，温和无刺激，儿童使用更安全。<br />\n	●&nbsp; 香香苹果味，让宝宝爱上刷牙。</p>\n', '14.400', '12.000', '16.000', '[{\"name\":\"基本参数\",\"paramList\":[{\"name\":\"规格\",\"value\":\"50g\",\"valueList\":[]},{\"name\":\"成分\",\"value\":\"山梨（糖）醇、去离子水、水合硅石、纤维素胶、单氟磷酸钠、木糖醇、月桂醇硫酸钠、蔓越莓果粉、羟苯甲酯、羟苯丙酯、食用香精、CI 15985\",\"valueList\":[]},{\"name\":\"颜色\",\"value\":\"\",\"valueList\":[]},{\"name\":\"适用人群\",\"value\":\"\",\"valueList\":[]},{\"name\":\"产地\",\"value\":\"\",\"valueList\":[]}],\"paramNum\":5}]', '[{\"cost\":12,\"goodsLvPrices\":[],\"goods_id\":223,\"name\":\"嗳呵 儿童牙膏（乳牙）青苹果味50g\",\"price\":14.4,\"product_id\":223,\"sn\":\"0016095639\",\"specList\":[],\"specs\":\"\",\"specsvIdJson\":\"[]\",\"store\":0,\"weight\":50}]', null, null, '1330090056758', '1330090056758', '0', '0', '0', '0', '0', '', '', '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', '0', '0', '0', '0', '0', null, '0', '0', '0', '0', '0');
INSERT INTO `es_goods` VALUES ('224', '喜亲宝竹香炭防螨定型枕36*24cm（天绿色）', '0011012661', '73', '76', '38', 'normal', null, '900.000', '1', 'http://javashop3.javamall.com.cn/statics/attachment/goods/201202242132103413.jpg', 'http://javashop3.javamall.com.cn/statics/attachment/goods/201202242132103413.jpg', null, '<p>\n	<font color=\"#800000\" face=\"黑体\" size=\"3\"><strong>商品介绍</strong></font>&nbsp; <font color=\"#c0c0c0\" size=\"4\">Product</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\"><strong>相关参数：</strong><br />\n	&nbsp; 颜色：天绿色<br />\n	&nbsp; 用途：优质竹香炭防螨定型枕，有利于宝宝头部健康周正的定型。<br />\n	&nbsp; 规格：36*24cm<br />\n	&nbsp; 成分：套：100%竹纤维；填充物：竹香炭、蒙稷、荞麦壳、七孔棉<br />\n	&nbsp; 提示：三个月内的宝宝不适宜用枕头</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\"><strong>产品特色：</strong><br />\n	竹纤维枕套：<br />\n	&nbsp; ●&nbsp; 采用纯天然竹纤维，其具有天然防螨、抑菌、抗静电、防湿疹之功效，由于此纤维质地柔软，似&ldquo;绫罗绸缎&rdquo;般光滑，可以降低宝宝头部与枕头的摩擦，有效预防枕秃。</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">竹香炭防螨定型枕特点：<br />\n	&nbsp; ●&nbsp; 严格依照人体工程学裁剪设计，同时严格按照婴幼儿的头型精心缝制而成，长期使用，有益于枕出健康周正的头型，是完全适合婴幼儿使用的专业产品。</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">竹香炭防螨定型枕填充物：<br />\n	&nbsp; ●&nbsp; 竹香炭，竹香炭是天然竹子的化身，源于竹子而又高于竹子，是利用现代科技制造出的新型活性保健产品，其独特的活性能恒久释放负离子（从竹炭中产生的负离子 具有穿透作用，能松弛神经、消除疲劳；在室内，从竹炭中产生的负离子与空气中的颗粒物相撞，使其带电，这些带电的颗粒物容易附着在墙壁、镜面、地板上，达 到净化空气的目的，置身其中，让人感受徜徉于大自然的森林中一般，心神无限舒畅）和发射远红外线（竹炭能产生最适合人体吸收的远红外线波长，能使物体产生 微热，促进人体衰老细胞的活化，加快血液循环，改善人体内环境）；竹香炭多孔的三维结构能有效吸附汗水、口水并释放天然的竹香，闻之令人神清气爽。</font></p>\n', '99.000', '80.000', '126.000', '[{\"name\":\"基本参数\",\"paramList\":[{\"name\":\"规格\",\"value\":\"36*24cm\",\"valueList\":[]},{\"name\":\"颜色\",\"value\":\"天绿色\",\"valueList\":[]},{\"name\":\"材质\",\"value\":\"\",\"valueList\":[]},{\"name\":\"重量\",\"value\":\"\",\"valueList\":[]},{\"name\":\"包装\",\"value\":\"\",\"valueList\":[]}],\"paramNum\":5}]', '[{\"cost\":80,\"goodsLvPrices\":[],\"goods_id\":224,\"name\":\"喜亲宝竹香炭防螨定型枕36*24cm（天绿色）\",\"price\":99,\"product_id\":224,\"sn\":\"0011012661\",\"specList\":[],\"specs\":\"\",\"specsvIdJson\":\"[]\",\"store\":0,\"weight\":900}]', null, null, '1